import React from 'react';
import './Header.css';
import cx from 'classnames';
import { useAppContext } from "../AppContext";


import { useState } from 'react';


export const Header = () => {
    const [showMobileNav, toggleMobileNav] = useState(false);
    const { cart, toggleCart } = useAppContext();
    const calculateCartItems = () => {
      let total = 0;
      cart.forEach((item) => (total += item.quantity));
      return total;
    };
    function openCart(e) {
      e.preventDefault();
      toggleCart(true);
    }
    
    return (
       <div>
            <header>
                <nav className="navbar navbar-light desktop-nav">
                <button className="d-block d-lg-none menu-btn" onClick={() => {
            toggleMobileNav(true);
          }}>
                    <span></span>
                    <span></span>
                    <span></span>
                    </button>
                    <ul className="nav justify-content-end d-none d-lg-flex">
                        <li className="nav-item">
                            <a className="navbar-brand" href="/">
                         <img className="logo" src="/media/logo-20c2cb1d9d2bb6d2139d0e5cec3103bd.png" alt="LUMIN"/></a>
                         </li>
                         <li className="nav-item"><a className="nav-link active" aria-current="page" href="/">Shop</a></li>
                         <li className="nav-item"><a className="nav-link" href="/">About</a></li>
                         <li className="nav-item"><a className="nav-link" href="/">Support</a></li>
                         <li className="nav-item"><a className="nav-link" href="/">Blog</a></li>
                         </ul>
                         <ul className="nav justify-content-end">
                             <li className="nav-item"><a className="nav-link active" aria-current="page" href="/">Account</a></li>
                             <li className="nav-item">
                                 <a className="nav-link" href="/">
                                 <div className="cart-icon-wrapper">
                                     <img className="cart-icon" src="data:image/png;base64," alt="LUMIN"/>
                                     <span>{calculateCartItems()}</span>
                                     </div>
                                     </a>
                                     </li>
                                     <li className="nav-item" onClick={showMobileNav} >
                                         <select className="form-select" aria-label="Default select example">
                                             <option>EN</option>
                                             <option value="1">AR</option>
                                             <option value="2">FR</option>
                                             <option value="3">ES</option>
                                             </select>
                                             </li>
                                             </ul>
                                             </nav>
                                             <nav className={cx("mobile-nav", {show:showMobileNav})} >
                                                 <div className="mobile-nav-wrapper">
                                                     <a className="active" href="/">Shop</a>
                                                     <a href="/">About</a>
                                                     <a href="/">Support</a>
                                                     <button  onClick={() => {  toggleMobileNav(false);
                                                                                                              }}>
            
                                                         <svg viewBox="0 0 256 512" focusable="false" role="presentation">
                                                        <path fill="currentColor" d="M231.293 473.899l19.799-19.799c4.686-4.686 4.686-12.284 0-16.971L70.393 256 251.092 74.87c4.686-4.686 4.686-12.284 0-16.971L231.293 38.1c-4.686-4.686-12.284-4.686-16.971 0L4.908 247.515c-4.686 4.686-4.686 12.284 0 16.971L214.322 473.9c4.687 4.686 12.285 4.686 16.971-.001z"></path>
                                                        </svg>
                                                        </button>
                                                        </div>
                                                        <div className="categories-wrapper">
                                                            <a className="active" href="/">Skin</a>
                                                            <a href="/">Hair &amp; Body</a>
                                                            <a href="/">Sets</a>
                                                            <a href="/">Accessories</a>
                                                            <a href="/">Shop All</a>
                                                            </div>
                                                            </nav>
                                                            </header>
                                                            </div>
       
    )
}
export  default Header;
